CREATE TABLE `doctors` ( `doctor_id` INT NOT NULL AUTO_INCREMENT , `doctor_name` VARCHAR(50) NOT NULL , `specialisation` VARCHAR(50) NULL , `experience` INT NULL , `image` TEXT NULL , `email_id` VARCHAR(50) NOT NULL , `phone` VARCHAR(11) NOT NULL , `qualification` VARCHAR(50) NULL , `languages` TEXT NULL , `fees` DOUBLE(11,2) NOT NULL , `is_active` BOOLEAN NOT NULL DEFAULT TRUE , `created_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , `updated_at` TIMESTAMP on update CURRENT_TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , PRIMARY KEY (`doctor_id`)) ENGINE = InnoDB;