CREATE TABLE `patients` ( `patient_id` INT NOT NULL AUTO_INCREMENT , `name` VARCHAR(50) NOT NULL , `gender` INT NOT NULL , `dob` DATE NOT NULL , `area` TEXT NOT NULL , `phone` VARCHAR(11) NOT NULL , `email` VARCHAR(50) NOT NULL , `is_active` BOOLEAN NOT NULL DEFAULT TRUE , `created_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , `updated_at` TIMESTAMP on update CURRENT_TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , PRIMARY KEY (`patient_id`)) ENGINE = InnoDB;